css:
/* General Body Styling */
body {
  margin: 0;
  font-family: 'Arial', sans-serif;
  background: #f9f9f9;
}

/* Certificate Container Styling */
.certificate-container {
  display: flex;
  justify-content: center;
  align-items: flex-start;  /* Adjusted to align to the top */
  height: auto;
  min-height: 120vh;  /* Increased height to allow for more content */
  background: url('https://sl.bing.net/g0sIAUznGxg') no-repeat center center;
  background-size: cover; /* Ensure the background image scales to cover the element */
}

/* Certificate Box Styling */
.certificate {
  width: 80%;
  max-width: 800px;
  padding: 80px 50px;  /* Increased padding for more vertical space */
  background: rgba(255, 255, 255, 0.85);
  border: 10px solid #004aad;
  border-radius: 10px;
  text-align: center;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-top: 20px;  /* Added top margin for spacing */
}

/* Title Styling */
.certificate h2 {
  font-size: 36px;
  color: #004aad;
  margin-bottom: 20px;
}

.certificate h1 {
  font-size: 48px;
  color: #000;
  margin: 10px 0;
}

.certificate h3 {
  font-size: 24px;
  color: #333;
  margin: 10px 0;
}

.certificate p {
  font-size: 18px;
  color: #555;
}

/* Signature Section Styling */
.signature-section {
  margin-top: 50px;  /* Increased margin for more space */
  text-align: center;
}

.signature {
  display: inline-block;
  width: 300px;
}

.signature hr {
  margin: 0 0 10px;
  border: 1px solid #000;
}

/* Adjustments for Printing */
@media print {
  body {
    margin: 0;
    padding: 0;
  }

  .certificate-container {
    height: auto;
    background: none; /* Remove background image when printing */
  }

  .certificate {
    width: 100%;
    max-width: 100%; /* Allow for scaling when printing */
    padding: 50px;
    box-shadow: none; /* Remove box-shadow for printing */
    border: 1px solid #004aad;
  }

  .certificate h1 {
    font-size: 36px;
  }

  .certificate h2 {
    font-size: 24px;
  }

  .certificate p,
  .certificate h3 {
    font-size: 16px;
  }
}
